import React from "react";
import FormInput from '../form-input/form-input.component'
import "./sign-in.styles.scss";

interface MyProps {}
interface MyState {
  email?: string;
  password?: string;
}

class SignIn extends React.Component<MyProps, MyState> {
  constructor(props: MyProps) {
    super(props);

    this.state = {
      email: "",
      password: "",
    };
  }

  handleSubmit=event=>{
      event.preventDefault()

      this.setState({
          email:"",password:""
      })
  }

  handleChange=event=>{
      const{value,name}=event.target;
      this.setState({
          [name]:value
      })
  }

  render() {
    return (
      <div className="sign-in">
        <h2>I already have an accound</h2>
        <span>Sign in with your email and password</span>

        <form>
          <FormInput type="email" name="email" value={this.state.email} handleChange={this.handleChange} label="password" required />
          <label htmlFor="email">Email</label>
          <FormInput
            type="password"
            label="email"
            name="password"
            value={this.state.password}
            handleChange={this.handleChange}
            required
          />
          <label htmlFor="password">Password</label>
          <FormInput type="submit" value="Submit Form" />
        </form>
      </div>
    );
  }
}

export default SignIn;
